mapper.select:
SELECT

FROM sys_msg_receive a

WHERE a.id = #{id}

SELECT count(1) FROM sys_msg_receive a
and a.status=#{page.entity.status}
and a.update_by=#{page.entity.user.id}
and c.type = #{page.entity.msgSend.type}
and ( c.title like '%'||#{page.key}||'%' '%'+#{page.key}+'%' CONCAT('%',#{page.key},'%') or c.content like '%'||#{page.key}||'%' '%'+#{page.key}+'%' CONCAT('%',#{page.key},'%') )

SELECT
FROM sys_msg_receive a
and a.status=#{page.entity.status}
and a.update_by=#{page.entity.user.id}
and c.type = #{page.entity.msgSend.type}
and ( c.title like '%'||#{page.key}||'%' '%'+#{page.key}+'%' CONCAT('%',#{page.key},'%') or c.content like '%'||#{page.key}||'%' '%'+#{page.key}+'%' CONCAT('%',#{page.key},'%') )
ORDER BY ${page.orderBy} LIMIT ${page.pageNo*page.pageSize},${page.pageSize}

mapper.update:
update sys_msg_receive set
status=#{status}
where id=#{id}
